import {Link} from 'react-router-dom';
import TeacherSidebar from './TeacherSidebar';
import  {useEffect,useState} from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import Swal from 'sweetalert2';
const baseUrl='http://127.0.0.1:8000/api';
function AssignQuiz(){
    const [quizData,setquizData]=useState([]);
    const [courseData,setcourseData]=useState([]);
    const teacherId=localStorage.getItem('teacherId');
    const {course_id}=useParams();

    // console.log(teacherId);
    useEffect(()=>{
        try{
            axios.get(baseUrl+'/teacher-quiz/'+teacherId)
            .then((res)=>{ 
                setquizData(res.data);
            });
        }catch(error){ 
            console.log(error);
        } 
        try{
              axios.get(baseUrl+'/course/'+course_id)
              .then((res)=>{
                // console.log(res);
                setcourseData(res.data);              
              });
            }
        catch(error){
              console.log(error);
            }
    },[]);
    const assignQuiz=(quiz_id)=>{
      const _formData=new FormData();
      _formData.append('teacher',teacherId);
      _formData.append('course',course_id);
      _formData.append('quiz',quiz_id);
      try{
          axios.post(baseUrl+'/student-assign-course/',_formData,{
              headers:{
                  'content-type': 'multipart/form-data'
              }
          })
      .then((res)=>{
         if(res.status===200 || res.status===201){
          Swal.fire({
            title: 'Quiz has been successfully assigned',
            icon:'success',
            toast:true,
            timer: 5000,
            position: 'top-right',
            timerProgressBar:true,
            showConfirmButton:false
          });
          window.location.reload();
         }           
      });
    }catch(error){
      console.log(error);
    }
    }

const handleDeleteClick=(quiz_id)=>{
        Swal.fire({
            title:'confirm',
            text: 'Are you sure you want to delete this chapter?',
            icon:'info',
            confirmButtonText: 'Continue',
            showCancelButton:true
    
        }).then((result)=>{
            if(result.isConfirmed){
                try{
                    axios.delete(baseUrl+'/quiz/'+quiz_id)
                    .then((res)=>{
                        Swal.fire('success','Data has been deleted.');
                        try{
                            axios.get(baseUrl+'/teacher-quiz/'+teacherId)
                            .then((res)=>{
                                setquizData(res.data);
                            });
                }catch(error){
                    console.log(error);
                }
        });
    }catch(error){
        Swal.fire('error','Data has not been deleted!!.');
    }
    }else{
     Swal.fire('error','Data has not been deleted!!.');
    }
    });
    }
    // console.log(courseData);
    // console.log("this is me");
    return(
        <div className="container mt-4 ">
            <div className="row">
               <aside className="col-md-3">
               <TeacherSidebar/>
               </aside>
               <section className='col-md-9'>
               <div className='card'>
        <h5 className='card-header'> Assign Quiz <span></span>({courseData.title})</h5>
        <div className='card-body'>
            <table className='table table-bordered'>
                <thead>
                    <tr>
                        <th>Name</th>
                    
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {quizData.map((row,index)=>
                    <tr>
                    <td><Link to={'/all-questions/'+row.id}>{row.title}</Link>

                    </td>
                    
                    <td>
                    <button  onClick={()=>assignQuiz(row.id)}className='btn btn-success btn-sm  ms-2'>Assign Quiz</button>
                    </td>
                  
                  </tr>
                    )} 
                </tbody>
            </table>
        </div>

    </div>   
               </section>
            </div>
        </div>
    );
}
export default AssignQuiz;


