import Sidebar from './Sidebar';
import {Link,useParams} from 'react-router-dom';
import  {useEffect,useState} from 'react';
import axios from 'axios';
const baseUrl='http://127.0.0.1:8000/api';
function TakeQuiz(){
	const [courseData,setcourseData]=useState([]);
    const [questionData,setquestionData]=useState([]);
    const {quiz_id}=useParams();
	const studentId=localStorage.getItem('studentId');
   useEffect(()=>{
        document.title='Take Quiz';
        try{
            axios.get(baseUrl+'/quiz-questions/'+quiz_id)
            .then((res)=>{ 
                setquestionData(res.data);
            });
        }catch(error){ 
            console.log(error);
        } 
    },[]);
    return(
        <div className="container mt-4 ">
            <div className="row">
               <aside className="col-md-3">
               <Sidebar/>
               </aside>
               <section className='col-md-9'>
               <h4 className='mb-3 border-bottom pb-1'>Quiz Title</h4>
               <div className='card'>
        <h5 className='card-header'>Question Title</h5>
        <div className='card-body'>
        
            <table className='table table-bordered'>
                <tbody>
                	
                	<tr>
                         <td><button className='btn btn-outline-secondary'> Option 1</button></td>
	                </tr>
                    <tr>
                         <td><button className='btn btn-outline-secondary'> Option 1</button></td>
	                </tr>
                    <tr>
                         <td><button className='btn btn-outline-secondary'> Option 1</button></td>
	                </tr>
                    <tr>
                         <td><button className='btn btn-outline-secondary'> Option 1</button></td>
	                </tr>
                </tbody>
            </table>
        </div>

    </div>   
               </section>
            </div>
        </div>
    );
}
export default TakeQuiz;
