import {Link} from 'react-router-dom';
import axios from "axios";
import  {useEffect,useState} from 'react';
import {useParams} from 'react-router-dom';  //to get id of the course which help to fetch form database
const siteUrl='http://127.0.0.1:8000';
const baseUrl='http://127.0.0.1:8000/api';
function TeacherDetail(){
    const [courseData,setcourseData]=useState([]);
    const [teacherData,setteacherData]=useState([]);
    const [skillList,setskillList]=useState([]);
    let {teacher_id}=useParams(); 
    useEffect(()=>{
        try{
          axios.get(baseUrl+'/teacher/'+teacher_id)
          .then((res)=>{
            // console.log(res);
            setteacherData(res.data);
            setcourseData(res.data.teacher_courses);
            setskillList(res.data.skill_list); 
          });
        }
        catch(error){
          console.log(error);
        }
        document.title='Teacher Detail';
    },[]);
    return (
        <div className="container mt-4 ">
        <div className="row">
            <div className="col-4">
            <img src="/logo512.png" className="img-thumbnail" alt="Teacher Image"/>
            </div>
            <div className='col-8'>
                <h3>{teacherData.full_name}</h3>
                <p> {teacherData.detail}</p>
                <p><strong>Skills :&nbsp; </strong>
                {/* {skillList.map((skill,index)=>
                      <Link to ={'/teacher-skill-courses/${skill.trim()}/${teacherData.id}'} className='badge badge-pill text-dark bg-warning mr-2'>{skill.trim()}</Link> 
                    )} */}
                   {courseData.map((course,index)=>
                    <tr>
                    <td><Link to={'/all-chapters/'+course.id}>{course.title}</Link>
                    <hr/>
                     {course.course_rating && <span>Rating: {course.course_rating}/5</span>}
                     {!course.course_rating && <span>Rating: 0/5</span>}

                    </td>
                    <td><img src={course.featured_img} width="80" className='rounded' alt={course.title}></img></td>
                    <td><Link to={'/enrolled-students/'+course.id}>{course.total_enrolled_students}</Link></td>
                    <Link className='btn btn-info active btn-sm' to={'/edit-course/'+course.id}>Edit</Link>
                    <Link className='btn btn-success active btn-sm  ms-2' to={'/add-chapter/'+course.id}>Add Chapters</Link>
                    <td><button className='btn btn-danger btn-sm  ms-2'>Delete</button></td>
                  
                  </tr>
                    )}  
                    </p>
                <p><strong>Recents: <Link to="/category/php"><strong>React JS Course</strong></Link></strong></p>
                <p><strong>Rating : 4.5/5</strong></p>
            </div>
        </div>
        <div className="cards mt-5">
           <div className="card-header">
            <h5>Course List</h5>      
           </div>
           <div className="list-group list-group-flush">
            {/* {courseData.map((course,index)=>
             <Link to ={'/detail/${course.id}'} className='list-group-item list-group-item-action'>{course.title}</Link>
            )} */}

            </div>
          
        </div>
     </div>
    
    );
}
export default TeacherDetail;
