import {Link, useParams} from 'react-router-dom';
import TeacherSidebar from './TeacherSidebar';
import  react,{useEffect,useState} from 'react';
import axios from 'axios';
import Swal from 'sweetalert2';
const baseUrl='http://127.0.0.1:8000/api';

function CourseChapters(){ 
    const [chapterData,setChapterData]=useState([]);
    const [totalResult,settotalResult]=useState([0]);
    const {course_id}=useParams();
    // console.log(teacherId);
    useEffect(()=>{
        document.title='All Courses';
        try{
            axios.get(baseUrl+'/course-chapters/'+course_id)
            .then((res)=>{ 
                settotalResult(res.data.length); 
                setChapterData(res.data);
            });
        }catch(error){ 
            console.log(error);
        } 
    },[]);
    // console.log(chapterData);
    // const Swal=require('sweetalert2')
    const handleDeleteClick=(chapter_id)=>{
        Swal.fire({
            title:'confirm',
            text: 'Are you sure you want to delete this chapter?',
            icon:'info',
            confirmButtonText: 'Continue',
            showCancelButton:true

        }).then((result)=>{
            if(result.isConfirmed){
                try{
                    axios.delete(baseUrl+'/chapter/'+chapter_id)
                    .then((res)=>{
                        Swal.fire('success','Data has been deleted.');
                        try{
                            axios.get(baseUrl+'/course-chapters/'+course_id)
                            .then((res)=>{
                                settotalResult(res.data.length);
                                setChapterData(res.data);
                            });
                }catch(error){
                    console.log(error);
                }
        });
    }catch(error){
        Swal.fire('error','Data has not been deleted!!.');
    }
}else{
     Swal.fire('error','Data has not been deleted!!.');
}
});
}
    return(
        <div className="container mt-4 ">
        <div className="row">
           <aside className="col-md-3">
           <TeacherSidebar/>
           </aside>
           <section className='col-md-9'>
           <div className='card'>
    <h5 className='card-header'> All Chapters({totalResult}) <Link className='btn btn-success float' to={'/add-chapter/'+course_id}> Add Chapter</Link> </h5>
    <div className='card-body'>
    <table className='table table-bordered'>
                <thead>
                    <tr>
                        <th>Title</th>
                        <th>Video</th>
                        <th>Remarks</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {chapterData.map((chapter,index)=>
                    <tr>
                    <td><Link to={'/edit-chapter/'+chapter.id}>{chapter.title}</Link></td>
                    
                    <td>
                        <video controls width="150">
                            <source src={chapter.video.url}
                            type="video/webm"></source>
                            <source src={chapter.video.url}
                            type="video/mp4"></source>
                        </video>
                    </td>
                    <td>{chapter.remarks}</td>
                    <Link to={'/edit-chapter/'+chapter.id} className='btn btn-sm btn-info'><i class ="bi bi-pencil-square"></i></Link>
                    <td><button onClick={()=>handleDeleteClick(chapter.id)} className='btn btn-danger btn-sm'><i class ="bi bi-trash"></i></button>
                    
                    </td>
                    
                  </tr>
                    )} 
                </tbody>
            </table>
    </div>

</div>   
           </section>
        </div>
    </div>
    );
}
export default CourseChapters;
