import  {useEffect,useState} from 'react';
import axios from 'axios';
const baseUrl ='http://127.0.0.1:8000/api/teacher/';
function Register(){
    useEffect(()=>{
        document.title='User Register';
    });
    const [studentData,setstudentData]=useState({
        'full_name':'',
        'email':'',
        'password':'',
        'username':'',
        'interest':'',
        'status':''
    });

    const handleChange=(event)=>{ 
        setstudentData({
            ...studentData, //spread operator
            [event.target.name]:event.target.value
        });
    }

    const submitForm=()=>{
        const teacherFormData=new FormData();
        teacherFormData.append("full_name",studentData.full_name)
        teacherFormData.append("email",studentData.email)
        teacherFormData.append("password",studentData.password)
        teacherFormData.append("qualification",studentData.qualification)
        teacherFormData.append("mobile_no",studentData.mobile_no)
        teacherFormData.append("skills",studentData.skills)
    
        try{
            axios.post(baseUrl,teacherFormData).then((response)=>{
                setstudentData({
                    'full_name':'',
                    'email':'',
                    'password':'',
                    'qualification':'',
                    'mobile_no':'',
                    'skills': '',
                    'status': 'success'
                });
            });
        }catch(error){
            console.log(error);
            setstudentData({'status':'error'});
        }
    
    };
    return(
        <div className="container mt-4 ">
        <div className="row">
            <div className="col-6 offset-3">
                <div className="card">
                    <h5 className="card-header">User Register</h5>
                    <div className="card-body">
                    <form>
                        <div className="mb-3">
                            <label for="exampleInputFullname" className="form-label">Full Name</label>
                            <input type="text" name="full_name" value={teacherData.full_name} className="form-control" id="exampleInputname" aria-describedby="nameHelp"/>
                            <div id="nameHelp" className="form-text"></div>
                        </div>
                        <div className="mb-3">
                            <label for="exampleInputEmail1" className="form-label">Email</label>
                            <input onChange={handleChange} type="email" name='email' className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp"/>
                            <div id="emailHelp" className="form-text">We'll never share your email with anyone else.</div>
                        </div>
                        <div className="mb-3">
                            <label for="exampleInputFullname" className="form-label">Username</label>
                            <input onChange={handleChange} type="text" name="username" className="form-control" id="exampleInputname" aria-describedby="nameHelp"/>
                            <div id="nameHelp" className="form-text"></div>
                        </div>
                        <div className="mb-3">
                            <label for="exampleInputPassword1" className="form-label">Password</label>
                            <input onChange={handleChange} type="password" name='email' className="form-control" id="exampleInputPassword1"/>
                        </div>
                        <div className="mb-3">
                            <label for="exampleInputFullname" className="form-label">Interests</label>
                            <input onChange={handleChange} type="text" name="Interests" className="form-control" id="exampleInputname" aria-describedby="nameHelp"/>
                            <div id="nameHelp" className="form-text"></div>
                        </div>
                        <button onClick={submitForm} type="submit" className="btn btn-primary">Register</button>
                        </form>
                    </div>

                </div>
           </div>
            </div>
        </div>
    );

}
export default Register;
