import Sidebar from './Sidebar';
import {Link,useParams} from 'react-router-dom';
import  {useEffect,useState} from 'react';
import axios from 'axios';
const baseUrl='http://127.0.0.1:8000/api';
function StudentAssignments(){
	const [assignmentData,setassignmentData]=useState([]);
    const [assignmentStatus,setassignmentStatus]=useState('');
	const studentId=localStorage.getItem('studentId');
   useEffect(()=>{
        document.title='My Assignments';
        try{
            axios.get(baseUrl+'/my-assignments/'+ studentId)
            .then((res)=>{ 
                setassignmentData(res.data);
            });
        }catch(error){ 
            console.log(error);
        }
    },[]);
    const markAsDone=(assignment_id)=>{
        const _formData=new FormData();

        _formData.append('status',true);
        try{
            axios.put(baseUrl+'/update-assignment/'+assignment_id,_formData,{
                headers:{
                    'content-type': 'multipart/form-data'
                }
            })
        .then((res)=>{
           if(res.status===200 || res.status===201){
            Swal.fire({
              title: 'You have successfully completed this Assignment',
              icon:'success',
              toast:true,
              timer: 5000,
              position: 'top-right',
              timerProgressBar:true,
              showConfirmButton:false
            });
            setassignmentStatus('success');
           }
           
        });
      }catch(error){
        console.log(error);
      }
      }
    return(
        <div className="container mt-4 ">
            <div className="row">
               <aside className="col-md-3">
               <Sidebar/>
               </aside>
               <section className='col-md-9'>
               <div className='card'>
        <h5 className='card-header'> My Assignments</h5>
        <div className='card-body'>
            <table className='table table-bordered'>
                <thead>
                    <tr>
                        <th>Title</th>
                        <th>Detail</th>
                        <th>Teacher</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                	{assignmentData.map((row,index)=>
                	<tr>
	                    <td>{row.title}</td>
                        <td>{row.detail}</td>
	                    <td><Link to={`/teacher-detail/`+row.teacher.id}>{row.teacher.full_name}</Link></td>
                        <td>
                           { assignmentStatus!=='success' && <button onClick={markAsDone} className='btn btn-success'>Mark as Done</button>}
                           {assignmentStatus==='success' && <span className='badge bg'></span>}
                        </td>
	                </tr>
	                )}
                </tbody>
            </table>
        </div>

    </div>   
               </section>
            </div>
        </div>
    );
}
export default StudentAssignments;
